mapper.select:
      select * from cust_customer
	  
	  		
	  			and Cust_Name like "%"#{cust_customer.Cust_Name}"%"
	  		
	  		 
	  			and Cust_askerName like "%"#{cust_customer.Cust_askerName}"%"
	  		
	  		
	  			and Cust_QQ =#{cust_customer.Cust_QQ}
	  		
	  		
	  			and Cust_Telephone =#{cust_customer.Cust_Telephone}
	  		
	  		
	  			and Cust_Pay =#{cust_customer.cust_Pay}
	  		
	  		
	  			and Cust_youxiao =#{cust_customer.cust_youxiao}
	  		
	  		
	  			and Cust_Revisit =#{cust_customer.cust_Revisit}
	  		
	  		
	  		
	  			and Cust_Creationtime >=#{minCust_Creationtime}
	  		
	  		
	  			and Cust_Creationtime <=#{maxCust_Creationtime}
	  		
	  		
	  		
	  			and Cust_Doortime >=#{minCust_Doortime}
	  		
	  		
	  			and Cust_Doortime <=#{maxCust_Doortime}
	  		
	  		
	  		
	  			and Cust_RevisitDays >=#{minCust_RevisitDays}
	  		
	  		
	  			and Cust_RevisitDays <=#{maxCust_RevisitDays}
	  		
	  		
	  		
	  			and Cust_Paytime >=#{minCust_Paytime}
	  		
	  		
	  			and Cust_Paytime <=#{maxCust_Paytime}
	  		
	  		
	  		
	  			and Cust_entrytime >=#{minCust_entrytime}
	  		
	  		
	  			and Cust_entrytime <=#{maxCust_entrytime}
	  		 
	    
      limit #{page},#{pagesize}

       select count(Cust_ID) from cust_customer
	 
	  		
	  			and Cust_Name like "%"#{cust_customer.Cust_Name}"%"
	  		
	  		
	  			and Cust_askerName like "%"#{cust_customer.Cust_askerName}"%"
	  		
	  		 
	  		
	  			and Cust_QQ =#{cust_customer.Cust_QQ}
	  		
	  		
	  			and Cust_Telephone =#{cust_customer.Cust_Telephone}
	  		
	  		
	  			and Cust_Pay =#{cust_customer.cust_Pay}
	  		
	  		
	  			and Cust_youxiao =#{cust_customer.cust_youxiao}
	  		
	  		
	  			and Cust_Revisit =#{cust_customer.cust_Revisit}
	  		
	  		
	  			and Cust_Creationtime >=#{minCust_Creationtime}
	  		
	  		
	  			and Cust_Creationtime <=#{maxCust_Creationtime}
	  		
	  		
	  		
	  			and Cust_Doortime >=#{minCust_Doortime}
	  		
	  		
	  			and Cust_Doortime <=#{maxCust_Doortime}
	  		
	  		
	  		
	  			and Cust_RevisitDays >=#{minCust_RevisitDays}
	  		
	  		
	  			and Cust_RevisitDays <=#{maxCust_RevisitDays}
	  		
	  		
	  		
	  			and Cust_Paytime >=#{minCust_Paytime}
	  		
	  		
	  			and Cust_Paytime <=#{maxCust_Paytime}
	  		
	  		
	  		
	  			and Cust_entrytime >=#{minCust_entrytime}
	  		
	  		
	  			and Cust_entrytime <=#{maxCust_entrytime}

      select cut.*,cut.Cust_youxiao,ak.*,sta.* from (cust_customer cut INNER JOIN asker ak on cut.`Cust_Asker_ID`=ak.Asker_ID)
	  INNER JOIN staff sta on sta.Staff_ID = ak.Asker_Staff_ID
	  
	  		
	  			and Cust_Name like "%"#{Cust_Name}"%"
	  		
	  		
	  			and Cust_QQ =#{Cust_QQ}
	  		
	  		
	  			and Cust_Telephone =#{Cust_Telephone}
	  		
	  		
	  			and Cust_Pay =#{cust_Pay}
	  		
	  		
	  			and Cust_youxiao =#{cust_youxiao}
	  		
	  		
	  			and Cust_Revisit =#{cust_Revisit}
	  		
	  		
	  		
	  			and Cust_Creationtime >=#{minCust_Creationtime}
	  		
	  		
	  			and Cust_Creationtime <=#{maxCust_Creationtime}
	  		
	  		
	  		
	  			and Cust_Doortime >=#{minCust_Doortime}
	  		
	  		
	  			and Cust_Doortime <=#{maxCust_Doortime}
	  		
	  		
	  		
	  			and Cust_RevisitDays >=#{minCust_RevisitDays}
	  		
	  		
	  			and Cust_RevisitDays <=#{maxCust_RevisitDays}
	  		
	  		
	  		
	  			and Cust_Paytime >=#{minCust_Paytime}
	  		
	  		
	  			and Cust_Paytime <=#{maxCust_Paytime}
	  		
	  		
	  		
	  			and Cust_entrytime >=#{minCust_entrytime}
	  		
	  		
	  			and Cust_entrytime <=#{maxCust_entrytime}
	  		
	  
	  and cut.Cust_Asker_ID =1
      limit #{page},#{rows}

       select count(*) from (cust_customer cut INNER JOIN asker ak on cut.`Cust_Asker_ID`=ak.Asker_ID)
	  INNER JOIN staff sta on sta.Staff_ID = ak.`Asker_Staff_ID`
	 
	  		
	  			and Cust_Name like "%"#{Cust_Name}"%"
	  		
	  		
	  			and Cust_QQ =#{Cust_QQ}
	  		
	  		
	  			and Cust_Telephone =#{Cust_Telephone}
	  		
	  		
	  			and Cust_Pay =#{cust_Pay}
	  		
	  		
	  			and Cust_youxiao =#{cust_youxiao}
	  		
	  		
	  			and Cust_Revisit =#{cust_Revisit}
	  		
	  		
	  		
	  			and Cust_Creationtime >=#{minCust_Creationtime}
	  		
	  		
	  			and Cust_Creationtime <=#{maxCust_Creationtime}
	  		
	  		
	  		
	  			and Cust_Doortime >=#{minCust_Doortime}
	  		
	  		
	  			and Cust_Doortime <=#{maxCust_Doortime}
	  		
	  		
	  		
	  			and Cust_RevisitDays >=#{minCust_RevisitDays}
	  		
	  		
	  			and Cust_RevisitDays <=#{maxCust_RevisitDays}
	  		
	  		
	  		
	  			and Cust_Paytime >=#{minCust_Paytime}
	  		
	  		
	  			and Cust_Paytime <=#{maxCust_Paytime}
	  		
	  		
	  		
	  			and Cust_entrytime >=#{minCust_entrytime}
	  		
	  		
	  			and Cust_entrytime <=#{maxCust_entrytime}
	  		
	  
	  and cut.Cust_Asker_ID =1

select * from cust_customer


